def doubles(lon):
    return [ n * 2 for n in lon ]

def rev(lon):
    return [ lon[len(lon) - i - 1] for i in range(len(lon)) ]

def average(lon):
    return int(sum(lon) / len(lon))

if __name__ == '__main__':
    arr = [1, 2, 3, 4, 5]
    print(doubles(arr))
    print(rev(arr))
    print(average(arr))
